  private handleError(error: Error, errorValue: any) {
    console.error(error);
    return of(errorValue);
  }
   /**
   * GÃ©nÃ¨re un ID alÃ©atoire pour un PokÃ©mon.
   * @returns Un ID alÃ©atoire pour un PokÃ©mon.
   */
  getRandomPokemon(): Observable<Pokemon> {
    const randomId = Math.floor(Math.random() * 898) + 1; // Supposons qu'il y ait 898 PokÃ©mon dans l'API
    return this.getPokemonByIdOrName(randomId.toString());
  }
/**
   * RÃ©cupÃ¨re les dÃ©tails d'un PokÃ©mon en utilisant GraphQL.
   * @param name Le nom du PokÃ©mon Ã  rechercher.
   * @returns Un Observable contenant les dÃ©tails du PokÃ©mon.
   */
  getPokemonDetailsWithGraphQL(identifier: string): Observable<Pokemon> {
    const query = gql`
      query GetPokemon($name: String!) {
        pokemon(name: $name) {
          id
          name
          height
          weight
          sprites {
            front_default
          }
          types {
            type {
              name
            }
          }
          stats {
            base_stat
            stat {
              name
            }
          }
          evolutions {
            name
            image
          }
          moves {
            move {
              name
              level
            }
          }
          species {
            flavor_text_entries {
              flavor_text
              language {
                name
              }
            }
          }
        }
      }
    `;

    return this.apollo.query<any>({ query, variables: { name: identifier } }).pipe(
      map(data => this.mapPokemon(data?.data?.pokemon))
    );
  }
/**
   * Mapper les donnÃ©es du PokÃ©mon pour les normaliser en fonction de l'interface Pokemon.
   * @param data Les donnÃ©es brutes du PokÃ©mon.
   * @returns Un objet Pokemon avec les donnÃ©es mappÃ©es.
   */
    private mapPokemon(data: any): Pokemon {
    return {
      id: data.id,
      name: data.name,
      image: data.sprites.front_default,
      types: data.types.map((type: any) => ({
        name: type.type.name,
        color: this.getTypeColor(type.type.name) 
      })),
      description: data.description || "", 
      stats: data.stats.map((stat: any) => ({
        name: stat.stat.name,
        value: stat.base_stat
      })),
      evolutions: data.evolutions ? data.evolutions.map((evolution: any) => ({
        name: evolution.name,
        image: evolution.image 
      })) : [], 
      moves: data.moves.map((move: any) => ({
        name: move.move.name,
        level: move.version_group_details[0].level_learned_at // Mapping du niveau du mouvement
      }))
    };
  }



  getPokemonDetailsGraphQL(name: string): Observable<any> {
    const query = gql`
      query GetPokemonDetails($name: String!) {
        pokemon(name: $name) {
          description
          moves(limit: 3) {
            move {
              name
              level
            }
          }
          evolutions {
            name
            image
          }
        }
      }
    `;

    return this.apollo.query<any>({
      query,
      variables: { name }
    }).pipe(
      map(response => response.data.pokemon),
      catchError(error => {
        console.error('Error fetching Pokemon details:', error);
        return of(null);
      })
    );
  }
/**Extrait et retourne la description en anglais Ã  partir des donnÃ©es de l'espÃ¨ce.
   *@param speciesData Les donnÃ©es de l'espÃ¨ce du PokÃ©mon.
  *@returns La description en anglais du PokÃ©mon.
  */
  private getDescriptionFromGraphQL(speciesData: any): string {
    if (speciesData && speciesData.flavor_text_entries && speciesData.flavor_text_entries.length > 0) {
      // Parcourir les entrÃ©es de texte de saveur pour trouver la premiÃ¨re description en anglais
      for (const entry of speciesData.flavor_text_entries) {
          return entry.flavor_text;

      }
    }
    return 'chaine vide'; // Retourner une chaÃ®ne vide si aucune description en anglais n'est trouvÃ©e
  }

/**
   * Retourne la couleur associÃ©e Ã  un type de PokÃ©mon.
   * @param type Le type du PokÃ©mon.
   * @returns La couleur associÃ©e au type.
   */  private getTypeColor(type: string): string {
    switch (type) {
      case 'grass':
        return '#88BE5D';
      case 'fire':
        return '#EE803B';
      case 'water':
        return 'blue';
