import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { Pokemon } from '../model/Pokemon';
import { PokemonService } from '../service/pokemon.service';
import { Observable, Subject, debounceTime, distinctUntilChanged, switchMap } from 'rxjs';

@Component({
  selector: 'app-search-form',
  templateUrl: './search-form.component.html',
  styleUrls: ['./search-form.component.css']
})
export class SearchFormComponent implements OnInit {
  // PropriÃ©tÃ©s pour le champ de recherche et la gestion des erreurs
  searchTerm: string = '';
  error: boolean = false;

  constructor(
    private router: Router,
    private pokemonService: PokemonService
  ) {}

  ngOnInit(): void {
  }

  /**
   * MÃ©thode dÃ©clenchÃ©e lors de la recherche d'un PokÃ©mon.
   * Redirige vers la page de dÃ©tails du PokÃ©mon trouvÃ©.
   * GÃ¨re les erreurs en cas de problÃ¨me lors de la rÃ©cupÃ©ration du PokÃ©mon.
   * @returns void
   */
    onSearch(): void {
    // RÃ©initialiser l'indicateur d'erreur
    this.error = false;
    // VÃ©rifier si le terme de recherche n'est pas vide
    if (this.searchTerm.trim()) {
      // Appeler le service pour obtenir les dÃ©tails du PokÃ©mon par son nom ou son ID
      this.pokemonService.getPokemonByIdOrName(this.searchTerm.trim()).subscribe({
        next: (pokemon) => {
          // Rediriger vers la page de dÃ©tails du PokÃ©mon trouvÃ©
          this.router.navigate(['/page-pokemon', pokemon.id]);
        },
        error: (err) => {
          // GÃ©rer les erreurs en cas de problÃ¨me lors de la rÃ©cupÃ©ration du PokÃ©mon
          this.error = true;
          console.error(err);
          // Optionnellement, naviguer vers le composant NotFound si vous ne souhaitez pas gÃ©rer l'erreur dans ce composant
          this.router.navigate(['/not-found']);
        },
      });
    }
  }

/**
   * MÃ©thode dÃ©clenchÃ©e lors de la recherche d'un PokÃ©mon alÃ©atoire.
   * Redirige vers la page de dÃ©tails du PokÃ©mon alÃ©atoire avec l'indicateur 'random'.
   * @returns void
   */
    onRandom(): void {
    // Appeler le service pour obtenir un PokÃ©mon alÃ©atoire
    this.pokemonService.getRandomPokemon().subscribe(
      (pokemon) => {
        // Rediriger vers la page de dÃ©tails du PokÃ©mon alÃ©atoire avec l'indicateur 'random'
        this.router.navigate(['/page-pokemon', pokemon.id], { state: { random: true } });
      },
    );
  }
}

