import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { PageNotFoundComponent } from './page-not-found/page-not-found.component';
import { SearchFormComponent } from './search-form/search-form.component';
import { PokemonPageComponent } from './pokemon-page/pokemon-page.component';

const routes: Routes = [
  { path: 'search-form', component: SearchFormComponent },

   { path: 'not-found', component: PageNotFoundComponent },
   {path: "page-pokemon/:id", component: PokemonPageComponent},

   { path: 'page-pokemon', component: PokemonPageComponent },


{ path: '**', redirectTo: '/search-form' } // Redirection pour toutes les autres routes inexistantes];
]
@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }

import { NgModule } from '@angular/core';
import { ApolloModule, APOLLO_OPTIONS } from 'apollo-angular';
import { ApolloClientOptions, InMemoryCache } from '@apollo/client/core';
import { HttpLink } from 'apollo-angular/http';
const uri = 'https://beta.pokeapi.co/graphql/v1beta'; // URL de l'API GraphQL

export function createApollo(httpLink: HttpLink): ApolloClientOptions<any> {
  return {
    link: httpLink.create({ uri }),
    cache: new InMemoryCache(),
  };
}

@NgModule({
  imports: [
    ApolloModule
  ],
  providers: [
    {
      provide: APOLLO_OPTIONS,
      useFactory: createApollo,
      deps: [HttpLink],
    },
  ],
  exports: [ApolloModule] // Exportation de ApolloModule
})
export class GraphQLModule { }

